import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import {  v4 } from "uuid";
import api from "../api/alunos";
import "./App.css";
import Header from "./Header/Header.js";
import AddAluno from "./AddAluno/AddAluno.js";
import AlunoList from "./AlunoList/AlunoList.js";
import AlunoDetail from "./AlunoDetail/AlunoDetail.js";
import EditAluno from "./EditAluno/EditAluno.js";
import TurmaList from "./TurmaList/TurmaList.js";

function App() {
  const LOCAL_STORAGE_KEY = "alunos";
  const [alunos, setAlunos] = useState([]);
  const turmasList = [
    {
    nomeTurma: "Matematica"
  },
    {
    nomeTurma: "Portugues"
  },
  {
  nomeTurma: "Ingles"
},
{
nomeTurma: "Ciencias"
}
  ];
const [turmas, setTurmas] = useState([turmasList]);
 

  //Retrieve Alunos
  const retrieveAlunos = async () => {
    const response = await api.get("/alunos");
    return response.data;
  };

  const addAlunoHandler = async (aluno) => {
    console.log(aluno);
    const request = {
      id: v4(),
      ...aluno,
    };

    const response = await api.post("/alunos", request);
    console.log(response);
    setAlunos([...alunos, response.data]);
  };

  const updateAlunosHandler = async (aluno) => {
    const response = await api.put(`/alunos/${aluno.id}`, aluno);
    const { id, alunoNome, alunoSegNome, alunoCPF, alunoMatricula, alunoNota, nomeTurmaAluno } = response.data;
    setAlunos(
      alunos.map((aluno) => {
        return aluno.id === id ? { ...response.data } : aluno;
      })
    );
  };

  const removeAlunoHandler = async (id) => {
    await api.delete(`/alunos/${id}`);
    const newAlunoList = alunos.filter((aluno) => {
      return aluno.id !== id;
    });

    setAlunos(newAlunoList);
  };

  useEffect(() => {
    // const retriveContacts = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY));
    // if (retriveContacts) setContacts(retriveContacts);
    const getAllAlunos = async () => {
      const allAlunos = await retrieveAlunos();
      if (allAlunos) setAlunos(allAlunos);
    };

    getAllAlunos();
  }, []);

  useEffect(() => {
    //localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(contacts));
  }, [alunos]);

  return (
    <div className="ui container">
      <Router>
        <Header />
        <Switch>
          <Route
            path="/"
            exact
            render={(props) => (
              <div>
              <AlunoList
                {...props}
                alunos={alunos}
                getAlunosId={removeAlunoHandler} 
              />
             
             
              <TurmaList turmas={turmasList.nomeTurma} alunos={alunos}
              cursando={alunos.filter(aluno => aluno.nomeTurmaAluno === turmas.nomeTurma)} />
              </div>
            )}
          />
          <Route
            path="/add"
            render={(props) => (
              <AddAluno {...props} addAlunoHandler={addAlunoHandler} />
            )}
          />

          <Route
            path="/edit"
            render={(props) => (
              <EditAluno
                {...props}
                updateAlunosHandler={updateAlunosHandler} />
            )}
          />

          <Route path="/contact/:id" component={AlunoDetail} />
        </Switch>
      </Router>
    </div>
  );
}

export default App;
