import React from "react";
import { Link } from "react-router-dom";
import AlunoCard from "../AlunoCard/AlunoCard.js"
import ProfessorCard from "../ProfessorCard/ProfessorCard.js";

const ProfessorsList = (props) => {
  console.log(props);

  const deleteProfHandler = (id) => {
    props.getProfId(id);
  };

  const renderProfessorsList = props.alunos.map((aluno) => {
    return (
      <ProfessorCard
      aluno={aluno}
        clickHander={deleteAlunoHandler}
        key={aluno.id}
      />
    );
  });
  return (
    <div className="main">
      <h2>
        Aluno List
        <Link to="/add">
          <button className="ui button blue right">Add Aluno</button>
        </Link>
      </h2>
      <div className="ui celled list">{renderProfessorsList}</div>
    </div>
  );
};

export default ProfessorsList;
