import React, { useState, useEffect } from "react";

import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { uuid } from "uuidv4";
import api from "../api/alunos";
import "./App.css";
import Header from "./Header/Header";
import AddAluno from "./AddAluno/AddAluno";
import AlunoList from "./AlunoList/AlunoList";
import AlunoDetail from "./AlunoDetail/AlunoDetail";
import EditAluno from "./EditAluno/EditAluno";

function App() {
  const LOCAL_STORAGE_KEY = "alunos";
  const [alunos, setAlunos] = useState([]);
  const turmas = [
    {
        "id": "1",
        "nomeTurma": "Matemática",
        "moduloTurma": "",
        "professorTurma": ""
      },
      {
        "id": "2",
        "nomeTurma": "Português",
        "moduloTurma": "",
        "professorTurma": ""
      },
      {
        "id": "3",
        "nomeTurma": "Ciências",
        "moduloTurma": "",
        "professorTurma": ""
      },
      {
        "id": "4",
        "nomeTurma": "Inglês",
        "moduloTurma": "",
        "professorTurma": ""
      }
    ]
  //RetrieveContacts
  const retrieveAlunos = async () => {
    const response = await api.get("/alunos");
    return response.data;
  };

  const addAlunoHandler = async (aluno) => {
    console.log(aluno);
    const request = {
      id: uuid(),
      ...aluno,
    };

    const response = await api.post("/alunos", request);
    console.log(response);
    setAlunos([...alunos, response.data]);
  };

  const updateAlunosHandler = async (aluno) => {
    const response = await api.put(`/alunos/${aluno.id}`, aluno);
    const { id, alunoNome, alunoSegNome, alunoCPF, alunoSala, alunoMatricula, alunoNota } = response.data;
    setAlunos(
      alunos.map((aluno) => {
        return aluno.id === id ? { ...response.data } : aluno;
      })
    );
  };

  const removeAlunoHandler = async (id) => {
    await api.delete(`/alunos/${id}`);
    const newAlunoList = alunos.filter((aluno) => {
      return aluno.id !== id;
    });

    setAlunos(newAlunoList);
  };

  useEffect(() => {
    // const retriveContacts = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY));
    // if (retriveContacts) setContacts(retriveContacts);
    const getAllAlunos = async () => {
      const allAlunos = await retrieveAlunos();
      if (allAlunos) setAlunos(allAlunos);
    };

    getAllAlunos();
  }, []);

  useEffect(() => {
    //localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(contacts));
  }, [alunos]);

  return (
    <div className="ui container">
      <Router>
        <Header />
        <Switch>
          <Route
            path="/"
            exact
            render={(props) => (
              <AlunoList
                {...props}
                alunos={alunos}
                getAlunosId={removeAlunoHandler}
              />
            )}
          />
          <Route
            path="/add"
            render={(props) => (
              <AddAluno {...props} addAlunoHandler={addAlunoHandler} data={turmas}/>
            )}
          />

          <Route
            path="/edit"
            render={(props) => (
              <EditAluno
                {...props}
                updateAlunosHandler={updateAlunosHandler} data={turmas}/>
            )}
          />

          <Route path="/contact/:id" component={AlunoDetail} />
        </Switch>
      </Router>
    </div>
  );
}

export default App;
