import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import {  v4 } from "uuid";
import api from "../api/alunos";
import api2 from "../api/professors";
import "./App.css";
import Header from "./Header/Header.js";
import AddAluno from "./AddAluno/AddAluno.js";
import AlunoList from "./AlunoList/AlunoList.js";
import AlunoDetail from "./AlunoDetail/AlunoDetail.js";
import EditAluno from "./EditAluno/EditAluno.js";
import ProfessorCard from "./ProfessorCard/ProfessorCard";
import ProfessorsList from "./ProfessorsList/ProfessorsList";

function App() {
  const LOCAL_STORAGE_KEY = "alunos";
  const LOCAL_STORAGE_KEY_2 = "professors";
  
  const [professors, setProfessors] = useState([]);
  const [alunos, setAlunos] = useState([]);
 

  //Retrieve Alunos
  const retrieveAlunos = async () => {
    const response = await api.get("/alunos");
    return response.data;
  };  
  //Retrieve professores
  const retrieveProf = async () => {
    const response = await api2.get("/professors");
    return response.data;
  };

  const addProfHandler = async (professor) => {
    console.log(professor);
    const request = {
      profId: v4(),
      ...professor,
    };
    const response = await api2.post("/professors", request);
    console.log(response);
    setProfessors([...professors, response.data]);
  };

  const updateProfessorsHandler = async (professor) => {
    const response = await api2.put(`/professors/${professor.professorCPF}`, professor);
    const {  professorNome, profesorSegNome, professorCPF, professorTitulo, professorTurma, professorModuloTurma } = response.data;
    setAlunos(
      alunos.map((professor) => {
        return professor.professorCPF === professorCPF ? { ...response.data } : professor;
      })
    );
  };
  
  const removeProfHandler = async (professorCPF) => {
    await api2.delete(`/professors/${professorCPF}`);
    const newProfsList = professors.filter((professor) => {
      return professor.professorCPF !== professorCPF;
    });    
    setProfessors(newProfsList);
  };
  const addAlunoHandler = async (aluno) => {
    console.log(aluno);
    const request = {
      id: v4(),
      ...aluno,
    };

    const response = await api.post("/alunos", request);
    console.log(response);
    setAlunos([...alunos, response.data]);
  };

  const updateAlunosHandler = async (aluno) => {
    const response = await api.put(`/alunos/${aluno.id}`, aluno);
    const { id, alunoNome, alunoSegNome, alunoCPF, alunoMatricula, alunoNota, nomeTurmaAluno, moduloTurma } = response.data;
    setAlunos(
      alunos.map((aluno) => {
        return aluno.id === id ? { ...response.data } : aluno;
      })
    );
  };

  const removeAlunoHandler = async (id) => {
    await api.delete(`/alunos/${id}`);
    const newAlunoList = alunos.filter((aluno) => {
      return aluno.id !== id;
    });

    setAlunos(newAlunoList);
  };
  useEffect(() => {
    // const retriveContacts = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY));
    // if (retriveContacts) setContacts(retriveContacts);
    const getAllProfs = async () => {
      const allProfessors = await retrieveProf();
      if (allProfessors) setProfessors(allProfessors);
    };

    getAllProfs();
  }, []);

  useEffect(() => {
    //localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(contacts));
  }, [professors]);

  useEffect(() => {
    // const retriveContacts = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY));
    // if (retriveContacts) setContacts(retriveContacts);
    const getAllAlunos = async () => {
      const allAlunos = await retrieveAlunos();
      if (allAlunos) setAlunos(allAlunos);
    };

    getAllAlunos();
  }, []);

  useEffect(() => {
    //localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(contacts));
  }, [alunos]);




  return (
    <div className="ui container">
      <Router>
        <Header />
        <Switch>
          <Route
            path="/"
            exact
            render={(props) => (
              <div>
              <AlunoList
                {...props}
                alunos={alunos}
                getAlunosId={removeAlunoHandler} 
              />

              <ProfessorsList
              {...props}
              professors={professors}
              
              getProfId={removeProfHandler} />

             
              </div>
            )}
          />
          <Route
            path="/add"
            render={(props) => (
              <AddAluno {...props} addAlunoHandler={addAlunoHandler} />
            )}
          />

          <Route
            path="/edit"
            render={(props) => (
              <EditAluno
                {...props}
                updateAlunosHandler={updateAlunosHandler} />
            )}
          />

          <Route path="/aluno/:id" component={AlunoDetail} />
          
          <Route path="/professor/:professorCPF" component={AlunoDetail} />
        </Switch>
      </Router>
    </div>
  );
}

export default App;
