import React from "react";

import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { DropDown } from "../DropDown/DropDown";

class EditAluno extends React.Component {
  constructor(props) {
    super(props);
    const { id, alunoNome, alunoSegNome, alunoCPF, alunoSala, alunoMatricula, alunoNota } = props.location.state.aluno;
    this.state = {
      id,
      alunoNome,
      alunoSegNome, 
      alunoCPF,
      alunoSala, 
      alunoMatricula,
      alunoNota
    };
    const { turmas } = this.props.data
  }

  update = (e) => {
    e.preventDefault();
    /* if (this.state.alunoNome === "" || this.state.alunoMatricula === "" || this.state.alunoCPF === "" || this.state.alunoSala === "" ||
    this.state.alunoSegNome === "") {
      alert("All the fields are mandatory!");
      return;
    } */
    this.props.updateAlunoHandler(this.state);
    this.setState({ alunoNome: "", alunoMatricula: "", alunoSegNome: "", alunoCPF: "", alunoNota: "" });
    this.props.history.push("/");
  };
  render() {
    return (
      <div className="ui main">
        <h2>Edit Aluno</h2>
        <form className="ui form" onSubmit={this.update}>
          <div className="field">
            <label>alunoNome</label>
            <input
              type="text"
              name="alunoNome"
              placeholder="alunoNome"
              value={this.state.alunoNome}
              onChange={(e) => this.setState({ alunoNome: e.target.value })}
            />
          </div>
          <div className="field">
            <label>alunoMatricula</label>
            <input
              type="text"
              name="alunoMatricula"
              placeholder="alunoMatricula"
              value={this.state.alunoMatricula}
              onChange={(e) => this.setState({ alunoMatricula: e.target.value })}
            />
          </div>
          <div className="field">
            <label>alunoSegNome</label>
            <input
              type="text"
              name="alunoSegNome"
              placeholder="alunoSegNome"
              value={this.state.alunoSegNome}
              onChange={(e) => this.setState({ alunoSegNome: e.target.value })}
            />
          </div>
          <div className="field">
            <label>alunoCPF</label>
            <input
              type="text"
              name="alunoCPF"
              placeholder="alunoCPF"
              value={this.state.alunoCPF}
              onChange={(e) => this.setState({ alunoCPF: e.target.value })}
            />
          </div>
          <div className="field">
            <label>alunoNota</label>
            <input
              type="text"
              name="alunoNota"
              placeholder="alunoNota"
              value={this.state.alunoNota}
              onChange={(e) => this.setState({ alunoNota: e.target.value })}
            />
          </div>
          <div className="field">
          <label>Turma</label>
          <DropDown turmas={this.props.data}/>
          </div>
          <button className="ui button blue">Update</button>
        </form>
      </div>
    );
  }
}

export default EditAluno;
