import 'bootstrap/dist/css/bootstrap.css'

import './DropDown.css';


export const DropDown = (props) => {
  return (
    <div className='lista-games'>
      <select value={props.turmas.valor}>
        <option value={props.turmas[0].nomeTurma}>Matemática</option>
        <option value={props.turmas[1].nomeTurma}>Português</option>
        <option value={props.turmas[2].nomeTurma}>Ciências</option>
        <option value={props.turmas[3].nomeTurma}>Inglês</option>
      </select>
    </div>
    )
}
