import React from 'react'
import { turmasList } from '../App.js'


export default TurmasList = (props) => {

    /* const fundoVar = {backgroundColor: props.corSecundaria};  */ // criar variavel pro fundo para tornar mais manuseavel

    return (
        (props.alunos.length >0) && // ou ? e : '' no final (ternario)
        <section className='games-section'>
            <h3 style={{borderColor: props.corPrimaria, backgroundColor: props.corSecundaria, borderRadius: '4px' }}>{props.nome}</h3>
            <div className='alunos'>
            {props.alunos.map(aluno => <alunoCard 
            corDeFundo={props.corPrimaria}
            key={aluno.nome} nome={aluno.nome} position={aluno.position} tag={aluno.tag} Disctag={aluno.Disctag}
            imgFundo={aluno.imgFundo} game={aluno.game}
            />)}
            </div>
        </section>

    )

}
